//d) find the minimum value in this array [23123, 2, -328, 0]
let arrayOfValues = [23123, 2, -328, 0];
let smallValue = arrayOfValues[0];
for (let i=0; i<arrayOfValues.length; i++){
    if(arrayOfValues[i] < smallValue){
        smallValue=arrayOfValues[i];
    }
}
console.log(smallValue)

