const jadenCase = require('./main');


describe('jaden case', () => {

    test ('give error', () => {
         expect(jadenCase('Hello')).toBe("Hello")
    });

    test ('return empty string', () => {
        expect(jadenCase('')).toBe("")
    });   
    test ('capitalises first letter of an word', () => {
        expect(jadenCase('hello')).toBe("Hello")
    });
    test ('capitalises first letter of two words', () => {
        expect(jadenCase('hello world')).toBe('Hello World')
    });
    test ('capitalises first letter of words in a sentence', () => {
        expect(jadenCase('to die is the biggest flex today')).toBe('To Die Is The Biggest Flex Today')
    });
    test ('capitalises a sentence with an apostrophe', () => {
        expect(jadenCase(`I\'m the biggest flex`)).toBe(`I\'m The Biggest Flex`)
    });
    
})
